en;\n    text-overflow: ellipsis;\n    white-space: nowrap;\n    font-size: inherit;\n    font-weight: 400;\n    -webkit-box-flex: 1;\n        -ms-flex: 1;\n            flex: 1;\n}\n/* Cell Component */\n/* Header Component */\n/* Button Component */\n/* Tab Item Component */\n/* Tabbar Component */\n/* Navbar Component */\n/* Checklist Component */\n/* Radio Component */\n/* z-index */\n.mint-button {\n    -webkit-appearance: none;\n       -moz-appearance: none;\n            appearance: none;\n    border-radius: 4px;\n    border: 0;\n    box-sizing: border-box;\n    color: inherit;\n    display: block;\n    font-size: 18px;\n    height: 41px;\n    outline: 0;\n    overflow: hidden;\n    position: relative;\n    text-align: center\n}\n.mint-button::after {\n    background-color: #000;\n    content: \" \";\n    opacity: 0;\n    top: 0;\n    right: 0;\n    bottom: 0;\n    left: 0;\n    position: absolute\n}\n.mint-button:not(.is-disabled):active::after {\n    opacity: .4\n}\n.mint-button.is-disabled {\n    opacity: .6\n}\n.mint-button-icon {\n    vertical-align: middle;\n    display: inline-block\n}\n.mint-button--default {\n    color: #656b79;\n    background-color: #f6f8fa;\n    box-shadow: 0 0 1px #b8bbbf\n}\n.mint-button--default.is-plain {\n    border: 1px solid #5a5a5a;\n    background-color: transparent;\n    box-shadow: none;\n    color: #5a5a5a\n}\n.mint-button--primary {\n    color: #fff;\n    background-color: #26a2ff\n}\n.mint-button--primary.is-plain {\n    border: 1px solid #26a2ff;\n    background-color: transparent;\n    color: #26a2ff\n}\n.mint-button--danger {\n    color: #fff;\n    background-color: #ef4f4f\n}\n.mint-button--danger.is-plain {\n    border: 1px solid #ef4f4f;\n    background-color: transparent;\n    color: #ef4f4f\n}\n.mint-button--large {\n    display: block;\n    width: 100%\n}\n.mint-button--normal {\n    display: inline-block;\n    padding: 0 12px\n}\n.mint-button--small {\n    display: inline-block;\n    font-size: 14px;\n    pad