#index-box .mint-header {
  background: #464c5c;
}
#index-box .mint-header.is-fixed {
  height: 45px;
  line-height: 45px;
}
#index-box .mint-header-button.is-left {
  display: inline-block;
  width: 22px;
  height: 22px;
  background: url(../img/personal_icon.png) no-repeat center;
  background-size: contain !important;
}
#index-box .mint-header-button {
  -webkit-box-flex: 0.1;
      -ms-flex: 0.1;
          flex: 0.1;
}
#index-box .mint-header-title {
  font-size: 1.8rem;
  font-weight: bold;
}
#index-box .content {
  padding: 1% 2%;
}
#index-box .title {
  position: relative;
  font-size: 1.6rem;
  line-height: 2.5rem;
  font-weight: bold;
  text-align: left;
}
#index-box .title a {
  position: absolute;
  right: 1rem;
  top: 0.5rem;
  color: red;
}
#index-box .contract-list {
  display: -webkit-box;
  display: -ms-flexbox;
  display: flex;
  -ms-flex-wrap: wrap;
      flex-wrap: wrap;
  width: 98%;
  margin: 0 auto 3rem;
  border-bottom: 1px solid #ccc;
  padding-bottom: 3rem;
}
#index-box .contract-list a {
  position: relative;
  display: inline-block;
  width: 19%;
  margin: 0.5rem 3%;
  -ms-flex-negative: 0;
      flex-shrink: 0;
  height: auto;
  text-align: center;
}
#index-box .contract-list .mint-badge.is-error {
  position: absolute;
  right: -1rem;
  top: -0.8rem;
  display: inline-block;
  padding: 0 0.5rem;
  line-height: 1.6rem;
}
#index-box .contract-list img {
  display: block;
  width: 100%;
  border-radius: 1rem;
  -webkit-box-shadow: 0rem 0rem 1rem rgba(0, 0, 0, 0.2);
          box-shadow: 0rem 0rem 1rem rgba(0, 0, 0, 0.2);
}
#index-box .contract-list span {
  font-size: 1.2rem;
  line-height: 2.5rem;
}
#index-box .file-list {
  padding: 1rem 0;
}
#index-box .file-list a {
  position: relative;
  display: block;
  border-top: 1px solid #ccc;
}
#index-box .file-list h4 {
  font-size: 1.6rem;
  margin-top: 1rem;
  padding-left: 0.5rem;
  padding-right: 0.5rem;
  border-left: 2px solid #099;
}
#index-box .file-list .right-info {
  position: absolute;
  right: 1rem;
  top: 0;
  text-align: right;
}
#index-box .file-list .right-info strong {
  position: relative;
  display: inline-block;
  line-height: 2rem;
  background: #0fa0e9;
  color: #fff;
  padding-left: 1rem;
  padding-right: 0.5rem;
}
#index-box .file-list .right-info strong::before {
  content: "";
  position: absolute;
  left: -1px;
  top: 0;
  width: 0;
  height: 0;
  border-top: 12px solid transparent;
  border-left: 7px solid white;
  border-bottom: 12px solid transparent;
}
#index-box .file-list .right-info span {
  display: block;
  color: #666;
}
#index-box .file-list .tag-list {
  border-top: 1px solid #f1f1f1;
  margin-top: 1rem;
  padding-top: 1rem;
}
#index-box .file-list .tag-list span {
  margin-right: 0.5rem;
}
#index-box .file-list .tag-list span::before {
  content: "";
  display: inline-block;
  width: 1.2rem;
  height: 1.2rem;
  background: url(../img/icon_fa.png) no-repeat left center;
  background-size: contain;
  vertical-align: middle;
  margin-right: 0.5rem;
}
#index-box .file-list .tag-list span.qian:before {
  background: url(../img/icon_qian.png) no-repeat left center;
  background-size: contain;
}
